package com.nttdata.Variables;

public class MinimumValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int intvalue=Integer.MIN_VALUE;
		float floatValue=Float.MIN_VALUE;
		byte byteValue=Byte.MIN_VALUE;
		short shortvalue=Short.MIN_VALUE;
		long longValue=Long.MIN_VALUE;
	    double doubleValue=Double.MIN_VALUE;
	
	
	System.out.println("Value of Integer:"+ intvalue);
	System.out.println("Value of Float:"+ floatValue);
	System.out.println("Value of Byte:"+  byteValue);
	System.out.println("Value of Short:"+ shortvalue);
	System.out.println("Value of Long:"+ longValue);
	System.out.println("Value of Double:"+ doubleValue);
	
	}

}
