package com.nttdata.Variables;

public class VariablesConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int intvalue=Integer.MAX_VALUE;
		float floatValue=Float.MAX_VALUE;
		byte byteValue=Byte.MAX_VALUE;
		short shortvalue=Short.MAX_VALUE;
		long longValue=Long.MAX_VALUE;
	    double doubleValue=Double.MAX_VALUE;
	boolean aboolValue=Boolean.FALSE;
	
	System.out.println("Value of Integer:"+ intvalue);
	System.out.println("Value of Float:"+ floatValue);
	System.out.println("Value of Byte:"+  byteValue);
	System.out.println("Value of Short:"+ shortvalue);
	System.out.println("Value of Long:"+ longValue);
	System.out.println("Value of Double:"+ doubleValue);
	System.out.println("Value of Boolean:"+ aboolValue);


	
	}
	

}
